 #include "uart_jetson_device.hpp"

 #include "tap/drivers.hpp"
 
 namespace tap::communication::serial
 {
 UartJetsonDevice::UartJetsonDevice(Drivers *drivers) : drivers(drivers) {}
 
 void UartJetsonDevice::initialize() { drivers->uart.init<JETSON_UART_PORT, UART_BAUDE_RATE>(); }
 
 bool UartJetsonDevice::read(char &c)
 {
     return drivers->uart.read(JETSON_UART_PORT, &reinterpret_cast<uint8_t &>(c));
 }
 
 void UartJetsonDevice::write(char c) { drivers->uart.write(JETSON_UART_PORT, c); }
 
 void UartJetsonDevice::flush() { drivers->uart.flushWriteBuffer(JETSON_UART_PORT); }
 }  // namespace tap::communication::serial
 